import { createGlobalStyle } from "styled-components";

export default createGlobalStyle`
@font-face{
  font-family: 'radnika-next';
  src: url('/public/radnika-next')
  format('woff2');
  font-weight: normal;
  font-style: normal;
}

html{
  box-sizing: border-box;
  font-size: 10px;
}
*, *:before, *:after{
  box-sizing: inherit;
}
body {
  padding: 0;
  margin: 0;
  font-size: 1.5rem;
  line-height: 2;
}

 a{
   text-decoration:none;
   color: ${theme.black};
 }
`;
