//     line-height: 2;
//   }

//   a{
//     text-decoration:none;
//     color: ${theme.black};
//   }
// `;

export default function Page({ children }) {
  return (
    <ThemeProvider theme={theme}>
      <StyledPage>
        <Meta />
        <Header />
        <Inner>
          <h1>Global Style</h1>
          <div>{children}</div>
        </Inner>
      </StyledPage>
    </ThemeProvider>
  );
}
